-- Триггеры для контроля заполнения контактов в лидах
DELIMITER //
DROP TRIGGER IF EXISTS contact_required;
CREATE TRIGGER contact_required BEFORE INSERT ON leads
FOR EACH ROW
BEGIN
	IF (NEW.phone) is NULL AND NEW.email is NULL THEN
		SIGNAL SQLSTATE '45000' SET MESSAGE_TEXT = 'Need to provide contact information';
	END IF;
END//
DELIMITER ;
